    val listData: ArrayList<Musician>
        get() {
            val list = arrayListOf<Musician>()
            for (position in musicianNames.indices) {
                val musician = Musician()
                musician.name = musicianNames[position]
                musician.origin = musicianOrigins[position]
                musician.genre = musicianGenres[position]
                musician.activeYear = musicianActiveYears[position]
                musician.member = musicianMembers[position]
                musician.album = musicianAlbums[position]
                musician.detail = musicianDetails[position]
                musician.photo = musicianImages[position]
                list.add(musician)
            }
            return list
        }
}